#create database if not exists talent default charset utf8;

#use talent;

DROP TABLE IF EXISTS `bolehtml`;
CREATE TABLE `bolehtml` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `status` tinyint(6) unsigned NOT NULL DEFAULT 1 COMMENT '资讯信息状态，1编辑中,2已发布,3已结束',
  `title` varchar(500) DEFAULT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `release_time` bigint NOT NULL DEFAULT 0 COMMENT '发布时间',
  `created_at` bigint NOT NULL DEFAULT 0 COMMENT '创建时间',
  `updated_at` bigint NOT NULL DEFAULT 0 COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `release_time` (`release_time`),
  KEY `status` (`status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

INSERT INTO bolehtml (title, content) VALUES ('赣州市“苏区人才伯乐奖”申领暂行办法', '<!--<p>--><h1><span style="font-size: 24px;">赣州市“苏区人才伯乐奖”申领暂行办法</span></h1><p><span style="font-size: 24px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">为激发全社会招才引智动力，营造尊才爱才用才的良好氛围，按照市委、市政府《关于创新人才政策、推动人才发展体制机制改革的若干意见》（赣市发〔2017〕21号）文件规定，凡向我市单位、企业推荐高层次人才并最终全职引进的组织或个人，给予“苏区人才伯乐奖”奖励。为规范申领工作，制定本办法。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第1条 &nbsp;本办法所称引进高层次人才，是指从市外引进产业领军人才（A+类）、国内外顶尖人才（A类）、国家级高层次人才（B类）、省级高层次人才（C类）、市级高层次人才（D类）（详见赣市发〔2017〕21号文件人才分类目录）。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第二条 &nbsp;本办法所称组织或个人，指市内外各类机关企事业单位（含用人单位）、其他各类社会组织或机构以及公民个人，本市公职人员除外。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第三条 &nbsp;高层次人才在赣市发〔2017〕21号文件印发之日后，与我市单位、企业签订最低3年服务年限合同并全职到岗一年，可以进行申领和发放。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第四条 &nbsp;奖励标准。经评审认定，“苏区人才伯乐奖”按以下标准给予奖励：</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">（一）每成功推荐并最终全职引进一名产业领军人才（A+类）或国内外顶尖人才（A类），给予20万元人民币奖励。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">（二）每成功推荐并最终全职引进一名国家级高层次人才（B类），给予10万元人民币奖励。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">（三）每成功推荐并最终全职引进一名省级高层次人才（C类），给予5万元人民币奖励。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">（四）每成功推荐并最终全职引进一名市级高层次人才（D类），给予1万元人民币奖励。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">“苏区人才伯乐奖”原则上发放给用人单位。由用人单位转发给推荐并引进人才的组织或个人，奖金分配由双方协商确定。特殊情况的，推荐并引进人才的组织或个人可征得用人单位同意后，直接提出奖励申领。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第五条 &nbsp;备案登记。人才引进时，用人单位填写《引进高层次人才备案登记表》（附件1）上传“赣州人才管家”APP或交至市招才引智局（市委人才办，电话/传真：0797-8391200）进行备案登记。没有进行备案登记的，不适用本办法享受“苏区人才伯乐奖”。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第六条 &nbsp;申领。人才全职引进满一年（从备案登记之日起计算）进行申领。申领时，需提供①《“苏区人才伯乐奖”申领表》（附件2）；②引进人才的相关证明（含身份证明、资格证明）；③引进人才与我市用人单位、企业签订的全职工作合同（进入我市机关事业单位的录用证明）；④用人单位、企业的营业执照、单位法人登记证书、组织机构代码证书等；⑤荐才引才组织的营业执照、单位法人登记证书、组织机构代码证书等，个人荐才引才的，须提供个人身份证复印件；⑥其他有关证件或证明材料。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第七条 &nbsp;受理审核。市招才引智局（市委人才办）根据受理情况，分批次对申领材料进行审核。材料不齐备未通过审核的，可在补齐材料后重新提交申请。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第八条 &nbsp;组织评审。市招才引智局（市委人才办）根据申报情况，组织专门人员或提交人才评审认定委员会进行评审把关。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第九条 &nbsp;公示。对拟发放“苏区引才伯乐奖”对象名单及金额，在赣州市人民政府网、赣州市委组织部党务公开网、赣州市人力资源与社会保障网等官方网站和赣州市人才之家微信号进行分批公示。公示期间，如有举报投诉并经核查属不符合申领条件或弄虚作假的，不予发放。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十条 &nbsp;奖金拨付。经公示无异议的，由市招才引智局（市委人才办）提请市委人才工作领导小组审批并拨付奖励资金，同时颁发“苏区人才伯乐”证书。原则上公示结束后15个工作日内办结。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十一条 &nbsp;“苏区人才伯乐奖”采取“随时受理、集中办理”的方式进行，原则上每半年集中办理1次，特殊情况另行通知和办理。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十二条 &nbsp;“苏区人才伯乐奖”所需经费由受益财政承担，按照“就高、不重复”的原则执行。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十三条 &nbsp;一个引进人才只能由一个组织或个人申领，且只能申领一次。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十四条 &nbsp;有关组织（机构）或个人弄虚作假，骗取、套取“苏区人才伯乐奖”奖金的，按规定予以追回并取消其申领资格；涉嫌违法犯罪的，移交司法机关依法处理。</span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 16px;"><br/></span></p><p><span style="font-family: monospace; white-space: pre-wrap; font-size: 20px;">第十五条 &nbsp;本办法自印发之日起实施，由市招才引智局（市委人才办）负责解释。</span><span style="font-family: monospace; white-space: pre-wrap; font-size: 16px;"><br/></span></p>');